import React, { useState } from 'react';

function SubscribePopup({ onClose }) {
    return (
        <div className="subscribe-popup">
            <div className="subscribe-popup-content">
                <h2>Subscribe to get important updates</h2>
                <input type="text" name="" id="" className="form-control" placeholder="Enter your email" />
                <button className="btn-style my-3" onClick={onClose}>Subscribe</button>
            </div>
        </div>
    );
}

function Footer() {
    const [isSubscribeOpen, setSubscribeOpen] = useState(false);

    const toggleSubscribePopup = () => {
        setSubscribeOpen(!isSubscribeOpen);
    };

    return (
        <>
            <footer>
                {/* Your existing footer content */}
                <button className="btn-style my-3" onClick={toggleSubscribePopup}>Subscribe</button>
            </footer>

            {isSubscribeOpen && <SubscribePopup onClose={toggleSubscribePopup} />}
        </>
    );
}

export default Footer;
